  'Content-Type': 'application/json',
  accept: 'application/json',
},
});

axiosInstance
        .post(`messages/`, {
            message: newMessage, // CONTEXT API
        })

  };
*/
  return (
    <div className="App">
      <Navbar/>
      <SearchAndBackground/>
      <SlideShow/>
      <ActionMenu/>
      <Widget
          handleNewUserMessage={handleNewUserMessage}
          title="Royar Help Desk"
          subtitle="online"
        />
      <Contact/>}
      <Footer/>
    </div>
  );
}
