import './App.css';
import Navbar from './Navbar'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import './css/main.css';
import SlideShow from './SlideShow'
import SearchAndBackground from './SearchAndBackground'
import ActionMenu from './ActionMenu'
import Contact from './Contact'
import Footer from './Footer'
import {Widget} from 'react-chat-widget'
import { StreamChat } from 'stream-chat';
import 'react-chat-widget/lib/styles.css';
import 'stream-chat-react/dist/css/index.css';
import {useEffect, useState, useCallback, useRef} from 'react'
import {
  Attachment,
  Chat,
  Channel,
  ChannelHeader,
  ChannelList,
  LoadingIndicator,
  MessageInput,
  MessageList,
  Thread,
  Window,
} from 'stream-chat-react';


const chatClient = StreamChat.getInstance('2myywf7yfbk8');
const userToken = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoic2lsZW50LWxpbWl0LTcifQ.E12VlZepuaIChIwoBWtUu9LNYnb403XXsTSu-T5RFBY';

chatClient.connectUser(
  {
    id: 'silent-limit-7',
    name: 'silent-limit-7',
    image: 'https://getstream.io/random_png/?id=silent-limit-7&name=silent-limit-7',
  },
  userToken,
);

const channel = chatClient.channel('livestream', 'spacex', {
  image: 'https://goo.gl/Zefkbx',
  name: 'SpaceX launch discussion',
});

export default function App() {

  const [chatClient, setChatClient] = useState(null);

  useEffect(() => {
    const initChat = async () => {
      const client = StreamChat.getInstance('2myywf7yfbk8');

      await client.connectUser(
        {
          id: 'silent-limit-7',
          name: 'silent-limit-7',
          image: 'https://getstream.io/random_png/?id=silent-limit-7&name=silent-limit-7',
        },
        userToken,
      );

      const [channelResponse] = await client.queryChannels(filters, sort);

      await channelResponse.sendMessage({
        text: 'Your selected product is out of stock, would you like to select one of these alternatives?',
        attachments,
      });

      setChatClient(client);
    };

    initChat();
  }, []);

  if (!chatClient) {
    return <LoadingIndicator />;
  }


  return (
    <div className="App">
      <Navbar/>
      <SearchAndBackground/>
      <SlideShow/>
      <ActionMenu/>
            <Chat client={chatClient} theme='livestream dark'>
        <Channel channel={channel}>
          <Window>
            <ChannelHeader live />
            <VirtualizedMessageList />
            <MessageInput Input={MessageInputSmall} focus />
          </Window>
        </Channel>
      </Chat>
      <Contact/>}
      <Footer/>
    </div>
  );
}
