import './App.css';
import Navbar from './Navbar'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import './css/main.css';
import SlideShow from './SlideShow'
import SearchAndBackground from './SearchAndBackground'
import ActionMenu from './ActionMenu'
import Contact from './Contact'
import Footer from './Footer'
import {Widget} from 'react-chat-widget'
import { StreamChat } from 'stream-chat';
import 'stream-chat-react/dist/css/index.css';
import {useEffect, useState, useCallback, useRef} from 'react'
import { Chat, Channel, ChannelHeader, MessageInput, MessageInputSmall, VirtualizedMessageList, Window } from 'stream-chat-react';



const chatClient = StreamChat.getInstance('2myywf7yfbk8');
const userToken = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoic2lsZW50LWxpbWl0LTcifQ.E12VlZepuaIChIwoBWtUu9LNYnb403XXsTSu-T5RFBY';

chatClient.connectUser(
  {
    id: 'silent-limit-7',
    name: 'silent-limit-7',
    image: 'https://getstream.io/random_png/?id=silent-limit-7&name=silent-limit-7',
  },
  userToken,
);

const channel = chatClient.channel('livestream', 'spacex', {
  image: 'https://goo.gl/Zefkbx',
  name: 'SpaceX launch discussion',
});

export default function App() {


  return (
    <div className="App">
      <Navbar/>
      <SearchAndBackground/>
      <SlideShow/>
      <ActionMenu/>
      <Chat className="chat" client={chatClient} theme='livestream dark'>
        <Channel channel={channel}>
          <Window>
            <ChannelHeader live />
            <VirtualizedMessageList />
            <MessageInput Input={MessageInputSmall} focus />
          </Window>
        </Channel>
      </Chat>
      <Contact/>}
      <Footer/>
    </div>
  );
}
