import React, { Component } from "react";
import { Menu, Dropdown, Input, Button } from "semantic-ui-react";
import 'semantic-ui-css/semantic.min.css'


export default class Price extends Component {
  state = { isOpen: false };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const { activeItem } = this.state;

    return (
    <div>
      <Menu secondary>
       <Menu.Item>
        <Dropdown
          text={"Price"}
          className="item"
          onClick={this.toggleOpen}
          open={this.state.isOpen}
        >
          <Dropdown.Menu onClick={this.preventClosing}>
            <Dropdown.Header icon="money" content={" Price Range"} />
            <Dropdown.Divider />
            <form
              style = {{margin: '10px'}}
              name="id"
              type="number"
              max={900}
              action={<Button onClick={this.toggleOpen} content={"Send"} />}
            >
            <Input style={{zoom: '0.9'}} placeholder="min"/>&nbsp;â€”&nbsp;<Input style={{zoom: '0.9'}} placeholder="max"/>
            </form>
          </Dropdown.Menu>
        </Dropdown>
        </Menu.Item>
       </Menu>
      </div>
    );
  }

  toggleOpen = () => this.setState({ isOpen: !this.state.isOpen });
  preventClosing = e => e.stopPropagation();
}
