import axiosInstance from './axios-connection'

var credentials = []
export function returnCredentials(){
  //console.log("returning credentials: ", credentials)
  return credentials
}

function setCredentials(access, refresh){
  credentials[0] = access
  credentials[1] = refresh
}

export const login = () => {
  axiosInstance
        .post(`core/token/`, {
            username: "ahmet", // CONTEXT API
            password: "ahmet",
        })
        .then((res) => {
            var access = res.data.access
            var refresh = res.data.refresh
            setCredentials(access,refresh)
            //console.log("last line of then(): ",credentials)
        })
        .catch((err)=>{
            console.log('error: ', err)
        })
}

export default login;
