import './App.css';
import Navbar from './Navbar'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import './css/main.css';
import SlideShow from './SlideShow'
import SearchAndBackground from './SearchAndBackground'
import ActionMenu from './ActionMenu'
import Contact from './Contact'
import Footer from './Footer'
import {Widget} from 'react-chat-widget'
import { StreamChat } from 'stream-chat';
import 'react-chat-widget/lib/styles.css';
import 'stream-chat-react/dist/css/index.css';
import {useEffect, useState, useCallback, useRef} from 'react'
import { Chat, Channel, ChannelHeader, ChannelList, LoadingIndicator, MessageInput, MessageList, Thread, Window } from 'stream-chat-react';


const chatClient = StreamChat.getInstance('2myywf7yfbk8');
const userToken = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoic2lsZW50LWxpbWl0LTcifQ.E12VlZepuaIChIwoBWtUu9LNYnb403XXsTSu-T5RFBY';

chatClient.connectUser(
  {
    id: 'silent-limit-7',
    name: 'silent-limit-7',
    image: 'https://getstream.io/random_png/?id=silent-limit-7&name=silent-limit-7',
  },
  userToken,
);

const channel = chatClient.channel('messaging', 'silent-limit-7', {
  // add as many custom fields as you'd like
  image: 'https://www.drupal.org/files/project-images/react.png',
  name: 'Talk about React',
  members: ['silent-limit-7'],
});


function App() {

  const [chatClient, setChatClient] = useState(null);

  useEffect(() => {
    const initChat = async () => {
      const client = StreamChat.getInstance('2myywf7yfbk8');

      await client.connectUser(
        {
          id: 'silent-limit-7',
          name: 'silent-limit-7',
          image: 'https://getstream.io/random_png/?id=silent-limit-7&name=silent-limit-7',
        },
        userToken,
      );

      setChatClient(client);
    };

    initChat();
  }, []);

  if (!chatClient) {
    return <LoadingIndicator />;
  }


  return (
    <div className="App">
      <Navbar/>
      <SearchAndBackground/>
      <SlideShow/>
      <ActionMenu/>
      <Widget/>
      <Contact/>}
      <Footer/>
    </div>
  );
}

export default App;
