export default function messageHandler(message){

  message =""+message
  const str = message.toUpperCase()
  console.log(str)
  var response = ''

  if(str.includes("BUY")){
    response = "Where would you like to buy from? Our options are currently Istanbul, Ankara, Izmir, and Antalya"
  }else if(str.includes("RENT")){
    response = "Where would you like to rent from?"
  }
  else if(str.includes("SELL")){
    response = "Where would you like to sell from?"
  }
  return response
}
