import React, {useState, useContext, useEffect} from 'react';
import {useHistory} from 'react-router-dom'
import {login, returnCredentials} from './auth';
import '../css/Login.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {FaUserCircle, FaKey} from "react-icons/fa"
import Eval from './img/eval.jpeg'





var hasLoginned = false;


export default function Login(){

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const {tokenValue} = useContext();
  const [token, setToken] = tokenValue

  useEffect(()=>{
    if(hasLoginned){
      console.log("token=",token);

      if(token){
        window.location.href = '/success'
      }
    };
  }, [token])

  const loginClicked = () => {
    login(username, password) // stores JSON object with access and refresh tokens.
    var credentials = returnCredentials()
    hasLoginned = true
    window.setTimeout(function(){
      console.log("credentials: ",credentials, "it has type of ", typeof(credentials))
      console.log(credentials[0])
      setToken(credentials[0])
    },2000)
  }


return (
  <div className="container h-100">
		<div className="d-flex justify-content-center h-100">
			<div className="user_card">
				<div className="d-flex justify-content-center">
					<div className="brand_logo_container">
						<img src= {Eval} className="brand_logo" alt="Logo"/>
					</div>
				</div>
				<div className="d-flex justify-content-center form_container">
					<form>
						<div className="input-group mb-3">
							<div className="input-group-append">
                <span class="input-group-text"><FaUserCircle className="icon" size={20}/></span>
							</div>
							<input type="text" name="" className="form-control input_user" placeholder="username"/>
						</div>
						<div className="input-group mb-2">
							<div className="input-group-append">
                <span class="input-group-text"><FaKey className="icon" size={20}/></span>
							</div>
							<input type="password" name="" className="form-control input_pass" placeholder="password"/>
						</div>
						<div className="form-group">
							<div id="rememberWrapper" className="custom-control custom-checkbox">
								<input type="checkbox" className="custom-control-input" id="customControlInline"/>
								<label id="rememberme" className="custom-control-label" for="customControlInline">Remember me</label>
							</div>
						</div>
							<div className="d-flex justify-content-center mt-3 login_container">
				 	<button type="button" name="button" className="btn btn-primary btn-lg" onClick={loginClicked}>Login</button>
				   </div>
					</form>
				</div>

				<div className="mt-4">
					<div className="d-flex justify-content-center links">
						Don't have an account? <a style={{textDecoration: 'none',}} href="#" className="ml-2">&nbsp; Sign Up</a>
					</div>
					<div className="d-flex justify-content-center links">
						<a style={{textDecoration: 'none',}} href="#">Forgot your password?</a>
					</div>
				</div>
			</div>
		</div>
	</div>
)
}
